mapper.select:
SELECT t1.authorize_id, t1.authorize_time, t1.user_id, t1.role_id, t1.user_group_id, t3.expression_id, t3.expression_name, t3.expression, t3.description exp_desc FROM t_authorize t1 LEFT JOIN t_authorize_cond t2 ON t2.authorize_id = t1.authorize_id LEFT JOIN t_permission_exp t3 ON t3.expression_id = t2.permission_exp_id WHERE permission_id = #{permissionId}
AND ( user_id IN (SELECT user_id FROM t_user WHERE username LIKE CONCAT('%', #{keyword}, '%')) OR role_id IN (SELECT role_id FROM t_role WHERE role_name LIKE CONCAT('%', #{keyword}, '%')) OR user_group_id IN (SELECT user_group_id FROM t_user_group WHERE user_group_name LIKE CONCAT('%', #{keyword}, '%')) )
ORDER BY authorize_time DESC
