mapper.select:
WITH filtered_users AS ( SELECT t_user.user_id FROM t_user LEFT JOIN t_user_attr_mapping ON t_user_attr_mapping.user_id = t_user.user_id LEFT JOIN t_user_attr ON t_user_attr.attr_id = t_user_attr_mapping.attr_id ${ew.customSqlSegment} GROUP BY t_user.user_id ORDER BY MAX(t_user.create_time) DESC
LIMIT #{limit} OFFSET #{offset}
) SELECT t1.user_id, t1.username, t1.phone_number, t1.email_address, t1.enable_mfa, t1.locked, t1.console_access, t1.create_time, t3.attr_id, t3.attr_key, t3.attr_name, t3.attr_data_type, t2.attr_value FROM filtered_users fu JOIN t_user t1 ON t1.user_id = fu.user_id LEFT JOIN t_user_attr_mapping t2 ON t2.user_id = t1.user_id LEFT JOIN t_user_attr t3 ON t3.attr_id = t2.attr_id ORDER BY t1.create_time DESC
