mapper.select:
SELECT t2.user_id, t2.username, t2.email_address, t2.phone_number FROM t_user_group_mapping t1 LEFT JOIN t_user t2 ON t2.user_id = t1.user_id WHERE t1.user_group_id = #{userGroupId}
AND( t2.username LIKE CONCAT('%', #{keyword}, '%') OR t2.email_address LIKE CONCAT ( '%', #{keyword}, '%' ) OR t2.phone_number LIKE CONCAT ( '%', #{keyword}, '%' ) )
ORDER BY t2.create_time DESC
